import React from 'react';

const Loader = (props) =>{
    console.log(props.error)
    try {
        if (props.error.search("403")){
            return(
                <h1 className="text-center">Maximum limit of Github Api reached</h1>
            )
    
        }
    } catch (error) {
        return(
            <h1 className="text-center">{props.errorMessage}Loading ...</h1>
        )
        
    }
    if (props.errorMessage.toString().search("403")){
        return(
            <h1 className="text-center">Maximum limit of Github Api reached</h1>
        )

    }
    return(
        <h1 className="text-center">{props.errorMessage}Loading ...</h1>
    )
}

export default Loader
