import React,{useState,useEffect} from 'react'
import '../css/user.css'
import axios from 'axios'
const User = ()=>{

        const [gitUserData,setGitUserData]=useState([])

        useEffect(()=>{

            getData()
        },[])

        const getData = async()=>{
            const data = await axios(
                {
                    method: 'get',
                    url: 'https://api.github.com/users?since=1',
                }
            ).then((response)=>{
                setGitUserData(response.data)
                console.log(response.data)
            })
            .catch((error)=>{
                alert(error)
            })
        }

    return(
        <>
        <div className="text-center container-fluid">
            <div className="row">
                <div className="col-12 col-md-12 my-3">
                    <h2>List Of Github  </h2>
                </div>
            </div>
           
                
            <div className="row my-5">
                
                
             {gitUserData.map((item,index)=>{
                 return(
                    <>
                    <div className="col-10 col-md-4 frame my-3">
                    <div className="row">
                        <div className="col-6 ">
                   <img src={item.avatar_url} alt="user_name" className="user_img my-1"/>
                   </div>
                   <div className="col-6 my-5">
                       <div className="row">
                           <div className="col-12">
                            <h3>Mod Zad</h3>
                            </div>
                        </div>
                        <div className="row">
                            <div className="col-12">
                                user info
                                </div>
                        </div>
                   </div>
                    </div>
                </div>
                    
                    </>
                 )
             })}
                
        


             
                
                
            </div>

            
        </div>
        </>
    )
}

export default User;
