import React from 'react';

const Loader = (props) =>{
    console.log(props.error.search("403"))
    
    if (props.error.toString().search("403") !==-1){
        return(
            <h1 className="text-center">Maximum limit of Github Api reached</h1>
        )

    }
    return(
        <h1 className="text-center">{props.errorMessage}Loading ...</h1>
    )
}

export default Loader
