import React from 'react';

const Loader = (props) =>{
    console.log("''''''''",props.error)
    console.log("''''''''",props.error.toString().search("403") )

    
    
    if (props.error.toString().search("403") !==-1 || props.error.toString().search("403") ==0){
        return(
            <h1 className="text-center">Maximum limit of Github Api reached</h1>
        )

    }
    else if(props.error === 404){
        return(
            <h1 className="text-center">Data Not Found</h1>)
    }
    
    

    return(
        <h1 className="text-center">Loading ...</h1>
    )
}

export default Loader
