import react,{useState,useEffect} from 'react';
import {NavLink} from 'react-router-dom'
import axios from 'axios'

const MyData = ()=>{

    const [username,setUsername] = useState("")
    const [userSeacrchedData,setUserSearchedData] = useState({})

    useEffect(()=>{
            setUsername("zadfab")
            getData()
        return(
            console.clear()
        )
    },[])

    useEffect(()=>{
        getData()
    },[username])

    const getData = async()=>{
    const userData = await axios(
       {
           method: 'GET',
           url:`https://api.github.com/users/${username}`,
       }
   ).then((response)=>{
       setUserSearchedData(response.data)
       console.log(response)
   }).catch((error)=>{
    console.log(error)
   })
    }

    return (
        <>
        
        <div className="row">
           <div className="container-fluid col-12 col-md-12 text-center my-5">
        <h1 >Search your user name</h1><NavLink activeClassName="" to="/"><span className="float-right mb-5">Go Back</span></NavLink>
        </div>
        </div>
        <div className="row">
            <div className="col-md-4"></div>
            <div className="col-12 col-md-4  form-group">
                <input className="form-control text-center" type="search" placeholder="Your username" value={username} onChange={(e)=>{return(setUsername(e.target.value))}}/>
            </div>
        </div>
        <div className="row">
           
                <div className=" col-12  col-md-5  my-5">
                    <img src={userSeacrchedData.avatar_url} alt="User_image" />
                </div>
                <div className=" col-12 col-md-5  my-5 text-capitalize text-left">
                    <h1 className='float-left'>{userSeacrchedData.name}</h1><h2  className='float-left'>{userSeacrchedData.location}</h2>   
                    
                    <h3>{userSeacrchedData.created_at}</h3>
                </div>


        </div>
    </> 
   )
}

export default MyData
